targets.expr:
1 - (
    pod_filesystem_avail_bytes{cluster="$cluster", namespace="$namespace", pod="$pod", mountpoint="/"} /
    pod_filesystem_size_bytes{cluster="$cluster", namespace="$namespace", pod="$pod", mountpoint="/"}
)

1 - 
(
    pod_filesystem_avail_bytes{cluster="$cluster", namespace="$namespace", pod="$pod", fstype!~"nfs.*|tmpfs"}
    /
    pod_filesystem_size_bytes{cluster="$cluster", namespace="$namespace", pod="$pod", fstype!~"nfs.*|tmpfs"}
)

1 - 
(
    pod_filesystem_files_free{cluster="$cluster", namespace="$namespace", pod="$pod", fstype!~"nfs.*|tmpfs"}
    /
    pod_filesystem_files{cluster="$cluster", namespace="$namespace", pod="$pod", fstype!~"nfs.*|tmpfs"}
)
